parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "base_id": {
      "type": "string",
      "description": "ID of the base to retrieve the schema for. Format - appHwXgLVrBujox4J"
    }
  },
  "required": ["base_id"]
}

{
  "type": "object",
  "properties": {
    "base_id": {
      "type": "string",
      "description": "ID of the base to search in"
    },
    "table_id": {
      "type": "string",
      "description": "ID of the table to search in"
    },
    "limit": {
      "type": "number",
      "description": "Number of records to retrieve (default is all records)"
    },
    "filter_desc": {
      "type": "string",
      "description": "Text description of the filter logic"
    },
    "sort": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "field": { "type": "string" },
          "direction": { "type": "string", "enum": ["asc", "desc"] }
        },
        "required": ["field", "direction"]
      },
      "description": "Array of sorting rules for the query"
    },
    "fields": {
      "type": "array",
      "items": { "type": "string" },
      "description": "List of fields to retrieve"
    }
  },
  "required": ["base_id", "table_id"]
}

{
  "type": "object",
  "properties": {
    "request": {
      "type": "string",
      "description": "Description of the operation to perform."
    },
    "data": {
      "type": "string",
      "description": "Stringified data - JSON, strings, arrays and etc."
    }
  },
  "required": ["request", "data"]
}

{
"type": "object",
"properties": {
	"markers": {
		"type": "string",
		"description": "List of markers with longitude and latitude data separated by comma. Keep the same color 555555|Example: pin-s+555555(-74.006,40.7128),pin-s+555555(-118.2437,34.0522)"
		}
	}
}

